package com.syntax.class08Homework;

public class ClockAnotherWay {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		for(int a=0; a<=2; a++) {
			for(int b=0; b<=9; b++){
				if(a==2&&b==4) {
					break;
					// just an example using break statement
					//totally unnecessary
				}for(int c=0; c<=5; c++) {
					for(int d=0; d<=9; d++) {
						System.out.println(a+""+b+":"+c+d);
					}
				}
			}
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
